import React, { Component } from "react";
import Navbar from "../../Components/navbar/navbar";
import "./Signup1.css";
import Box from "@mui/material/Box";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

// Define the state and props interfaces
interface Signup1State {
  accountType: string;
}

class Signup1 extends Component<{}, Signup1State> {
  constructor(props: {}) {
    super(props);
    this.state = {
      accountType: "", // Initial account type value
    };
  }

  // Handle dropdown change
  handleChange = (event: React.ChangeEvent<{ value: unknown }>) => {
    const selectedValue = event.target.value as string;
    this.setState({ accountType: selectedValue });

    // Pass the value to the backend (example)
    console.log("Selected Account Type:", selectedValue);
    // Example API call
    // fetch("/api/accountType", {
    //   method: "POST",
    //   headers: { "Content-Type": "application/json" },
    //   body: JSON.stringify({ accountType: selectedValue }),
    // });
  };

  render() {
    const { accountType } = this.state;

    return (
      <>
        <Navbar />
        <h1>Let's Get Started</h1>
        <section className="signup-main">
          <h2>Get Started</h2>
          <h3>What is your account type?</h3>
          <div className="custom-select">
            <Box
              sx={{
                minWidth: 300,
                margin: "0 auto",
                textAlign: "left",
                backgroundColor: "#FFF8F8",
                borderRadius: "10px",
                borderOutline: "none",
              }}
            >
              <FormControl fullWidth>
                <InputLabel id="account-type-label">Account Type</InputLabel>
                <Select
                  labelId="account-type-label"
                  id="account-type"
                  value={accountType}
                  onChange={this.handleChange}
                >
                  <MenuItem value="student">Student</MenuItem>
                  <MenuItem value="landlord">Landlord</MenuItem>
                </Select>
              </FormControl>
            </Box>
          </div>
        </section>
      </>
    );
  }
}

export default Signup1;
